contracts/libraries/LibTypes.sol.content:
// SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.13;

/**
 * @title A collection of defined structs
 * @notice This library defines the various data models that the Sarcophagus
 * system uses
 */
library LibTypes {
    // DoesNotExist must come first on the list to be the default value
    enum SarcophagusState {
        DoesNotExist,
        Exists,
        Done
    }

    // A struct of just the signature. This is used primarily by the
    // finalizeSarcpohagus function for the arweave archaeologist. Note that,
    // unlike the regular archaeologists, the sarcophagus already stores the
    // single arweave archaeologist's address so there is no need to pass in the
    // address to the finalizeSarcophagus function.
    struct Signature {
        uint8 v;
        bytes32 r;
        bytes32 s;
    }

    // Signature struct created to make passing in the signature argmuments into
    // finalizedSarcophagus easier and to avoid the "stack too deep" error.
    // Also attaching arachaeologist addresses so we can tie the signature back
    // to the address in finalizeSarcophagus.
    struct SignatureWithAccount {
        address account;
        uint8 v;
        bytes32 r;
        bytes32 s;
    }

    // ArchaeologistMemory is the struct that is passed into the
    // initializeSarcophagus function. Even though we don't need each storage
    // fee of the archaeologist, the storage fee is included in the struct to
    // reduce the stack size within the function, preventing the "stack too
    // deep" error.
    struct ArchaeologistMemory {
        address archAddress;
        uint256 storageFee;
        uint256 diggingFee;
        uint256 bounty;
        bytes32 hashedShard;
    }

    // ArchaeologistStorage is the struct that is stored in AppStorage under the
    // sarcophagusArchaeologists mapping.
    //
    // The archaeologist address is left out since each archaeologist's address
    // is stored on the sarcophagus object as an array.
    //
    // The storage fee is left out becuase we only need to store the storage fee
    // of the archaeologist uploading to arweave, which will be stored directly
    // on the sarcophagus.
    struct ArchaeologistStorage {
        uint256 diggingFee;
        uint256 bounty;
        bytes32 doubleHashedShard;
        bytes unencryptedShard;
    }

    // The ArchaeologistStorage struct could be contained in this Sarcophagus
    // struct as a mapping, but it was put into it's own mapping
    // (sarcophagusArchaeologists) directly in AppStorage. Instead the
    // sarcophagus stores the addresses of each archaeologist added to it. This
    // was done to simplify the creation of a sarcophagus object in
    // initializeSarcophagus.
    struct Sarcophagus {
        string name;
        SarcophagusState state;
        bool canBeTransferred;
        uint8 minShards;
        uint256 resurrectionTime;
        uint256 maxResurrectionInterval;
        string[] arweaveTxIds;
        uint256 storageFee;
        address embalmer;
        address recipientAddress;
        address arweaveArchaeologist;
        address[] archaeologists;
    }
}

contracts/libraries/LibErrors.sol.content:
// SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.13;

/**
 * @title A collection of Errors
 * @notice This library defines all of the Errors that the Sarcophagus system
 * uses.
 */
library LibErrors {
    error ArchaeologistAlreadyUnwrapped(address archaeologist);

    error ArchaeologistListNotUnique(address[] archaeologists);

    error ArchaeologistNotOnSarcophagus(address archaeologist);

    error ArweaveArchaeologistNotInList();

    error ArweaveTxIdEmpty();

    error IncorrectNumberOfArchaeologistSignatures(uint256 signaturesLength);

    error MinShardsGreaterThanArchaeologists(uint8 minShards);

    error MinShardsZero();

    error MaxResurrectionIntervalIsZero();

    error NewResurrectionTimeInPast(uint256 newResurrectionTime);

    error NewResurrectionTimeTooLarge(uint256 newResurrectionTime);

    error NoArchaeologistsProvided();

    error NotEnoughCursedBond(uint256 cursedBond, uint256 amount);

    error NotEnoughFreeBond(uint256 freeBond, uint256 amount);

    error NotEnoughReward(uint256 reward, uint256 amount);

    error ResurrectionTimeInPast(uint256 resurrectionTime);

    error SarcophagusAlreadyExists(bytes32 sarcoId);

    error SarcophagusAlreadyFinalized(bytes32 sarcoId);

    error SarcophagusNotFinalized(bytes32 sarcoId);

    error SarcophagusDoesNotExist(bytes32 sarcoId);

    error SenderNotEmbalmer(address sender, address embalmer);

    error SignatureFromWrongAccount(
        address hopefulAddress,
        address actualAddress
    );

    error SignatureListNotUnique();

    error SignerNotArchaeologistOnSarcophagus(bytes32 sarcoId, address signer);

    // Used when an attempt is made to accuse or rewrap after the resurrection time has already passed (so it's actually time to unwrap it)
    error SarcophagusIsUnwrappable();

    // Used when an attempt is made to clean a sarcophagus that has not exceeded its resurrection window
    error SarcophagusNotCleanable();

    // Used when accusing with not enough, or invalid, unencrypted shard(s)
    error NotEnoughProof();

    error TooEarlyToUnwrap(uint256 resurrectionTime, uint256 currentTime);

    error TooLateToUnwrap(
        uint256 resurrectionTime,
        uint256 resurrectionWindow,
        uint256 currentTime
    );

    error UnencryptedShardHashMismatch(
        bytes unencryptedShard,
        bytes32 doubleHashedShard
    );
}

contracts/libraries/LibBonds.sol.content:
// SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.13;

import "../storage/LibAppStorage.sol";
import "../libraries/LibTypes.sol";
import {LibErrors} from "../libraries/LibErrors.sol";

library LibBonds {
    /// @notice Calculates the cursed bond that an archaeologist needs to lock
    /// up
    /// @dev The cursed bond amount is the sum of the digging fee and the
    /// bounty.
    /// @param diggingFee The digging fee of the sarcophagus
    /// @param bounty The bounty of the sarcophagus
    /// @return The amount of cursed bond
    function calculateCursedBond(uint256 diggingFee, uint256 bounty)
        internal
        pure
        returns (uint256)
    {
        // TODO: Implement a better algorithm for calculating the cursed bond
        return diggingFee + bounty;
    }

    /// @notice Decreases the amount stored in the freeBond mapping for an
    /// archaeologist. Reverts if the archaeologist's free bond is lower than
    /// the amount.
    /// @param archaeologist The address of the archaeologist whose
    /// free bond is being decreased
    /// @param amount The amount to decrease the free bond by
    function decreaseFreeBond(address archaeologist, uint256 amount) internal {
        AppStorage storage s = LibAppStorage.getAppStorage();

        // Revert if the amount is greater than the current free bond
        if (amount > s.freeBonds[archaeologist]) {
            revert LibErrors.NotEnoughFreeBond(
                s.freeBonds[archaeologist],
                amount
            );
        }

        // Decrease the free bond amount
        s.freeBonds[archaeologist] -= amount;
    }

    /// @notice Increases the amount stored in the freeBond mapping for an
    /// archaeologist.
    /// @param archaeologist The address of the archaeologist whose
    /// free bond is being decreased
    /// @param amount The amount to decrease the free bond by
    function increaseFreeBond(address archaeologist, uint256 amount) internal {
        AppStorage storage s = LibAppStorage.getAppStorage();

        // Increase the free bond amount
        s.freeBonds[archaeologist] += amount;
    }

    /// @notice Decreases the amount stored in the cursedBond mapping for an
    /// archaeologist. Reverts if the archaeologist's cursed bond is lower than
    /// the amount.
    /// @param archaeologist The address of the archaeologist whose
    /// cursed bond is being decreased
    /// @param amount The amount to decrease the cursed bond by
    function decreaseCursedBond(address archaeologist, uint256 amount)
        internal
    {
        AppStorage storage s = LibAppStorage.getAppStorage();

        // Revert if the amount is greater than the current cursed bond
        if (amount > s.cursedBonds[archaeologist]) {
            revert LibErrors.NotEnoughCursedBond(
                s.cursedBonds[archaeologist],
                amount
            );
        }

        // Decrease the cursed bond amount
        s.cursedBonds[archaeologist] -= amount;
    }

    /// @notice Increases the amount stored in the cursedBond mapping for an
    /// archaeologist.
    /// @param archaeologist The address of the archaeologist whose
    /// cursed bond is being decreased
    /// @param amount The amount to decrease the cursed bond by
    function increaseCursedBond(address archaeologist, uint256 amount)
        internal
    {
        AppStorage storage s = LibAppStorage.getAppStorage();

        // Increase the cursed bond amount
        s.cursedBonds[archaeologist] += amount;
    }

    /// @notice Locks up the archaeologist's bond, decreasing the
    /// archaeologist's free bond by an amount and increasing the
    /// archaeologist's cursed bond by the same amount.
    /// @param archaeologist The address of the archaeologist
    /// @param amount The amount to lock up
    function lockUpBond(address archaeologist, uint256 amount) internal {
        // Decrease the free bond amount
        decreaseFreeBond(archaeologist, amount);

        // Increase the cursed bond amount
        increaseCursedBond(archaeologist, amount);
    }

    /// @notice Unlocks the archaeologist's bond, increasing the
    /// archaeologist's free bond by an amount and decreasing the
    /// archaeologist's cursed bond by the same amount.
    /// @param archaeologist The address of the archaeologist
    /// @param amount The amount to unlock
    function unlockBond(address archaeologist, uint256 amount) internal {
        // Decrease the cursed bond amount
        decreaseCursedBond(archaeologist, amount);

        // Increase the free bond amount
        increaseFreeBond(archaeologist, amount);
    }

    /// @notice Given an array of archaeologists on a sarcophagus, sums the total of
    /// 1. Each archaeologists' bounty
    /// 2. Each archaeologists' digging fees
    /// 3. The storage fee
    /// @param sarcoId The identifier of the sarcophagus
    /// @param archaeologists The array of archaeologists' addresses
    /// @return the total of the above
    function calculateTotalFees(
        bytes32 sarcoId,
        address[] memory archaeologists
    ) internal view returns (uint256) {
        AppStorage storage s = LibAppStorage.getAppStorage();

        uint256 totalFees = 0;

        // iterate through each archaeologist
        for (uint256 i = 0; i < archaeologists.length; i++) {
            LibTypes.ArchaeologistStorage memory archaeologistsData = s
                .sarcophagusArchaeologists[sarcoId][archaeologists[i]];

            // add the archaeologist's bounty to the total fees
            totalFees += archaeologistsData.bounty;

            // add the archaeologist's digging fee to the total fees
            totalFees += archaeologistsData.diggingFee;
        }

        // add the storage fee to the total fees
        totalFees += s.sarcophagi[sarcoId].storageFee;

        // return the total fees
        return totalFees;
    }

    /// @notice Calculates an archaeologist's cursed bond and curses them (locks
    /// up the free bond).
    /// @param sarcoId the identifier of the sarcophagus to bond the archaeologist with
    /// @param archaeologist the address of the archaeologist to curse
    function curseArchaeologist(bytes32 sarcoId, address archaeologist)
        internal
    {
        AppStorage storage s = LibAppStorage.getAppStorage();

        // Get the archaeologist's data from storage
        LibTypes.ArchaeologistStorage memory archaeologistData = s
            .sarcophagusArchaeologists[sarcoId][archaeologist];

        // Calculate the amount of cursed bond the archaeologists needs to lock up
        uint256 cursedBondAmount = calculateCursedBond(
            archaeologistData.diggingFee,
            archaeologistData.bounty
        );

        // Lock up the archaeologist's bond by the cursed bond amount
        lockUpBond(archaeologist, cursedBondAmount);
    }

    /// @notice Calculates an archaeologist's cursed bond and frees them
    /// (unlocks the cursed bond).
    /// @param sarcoId the identifier of the sarcophagus to free the archaologist from
    /// @param archaeologist the address of the archaeologist to free
    function freeArchaeologist(bytes32 sarcoId, address archaeologist)
        internal
    {
        AppStorage storage s = LibAppStorage.getAppStorage();

        // Get the archaeologist's data from storage
        LibTypes.ArchaeologistStorage memory archaeologistData = s
            .sarcophagusArchaeologists[sarcoId][archaeologist];

        // Calculate the amount of cursed bond the archaeologists needs to lock up
        uint256 cursedBondAmount = calculateCursedBond(
            archaeologistData.diggingFee,
            archaeologistData.bounty
        );

        // Lock up the archaeologist's bond by the cursed bond amount
        unlockBond(archaeologist, cursedBondAmount);
    }
}

contracts/libraries/LibRewards.sol.content:
// SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.13;

import "../storage/LibAppStorage.sol";
import "../libraries/LibTypes.sol";
import {LibErrors} from "../libraries/LibErrors.sol";

library LibRewards {
    /// @notice Decreases the amount stored in the archaeologistRewards mapping for an
    /// archaeologist. Reverts if the archaeologist's reward is lower than
    /// the amount. Called on reward withdraw.
    /// @param archaeologist The address of the archaeologist whose
    /// reward is being decreased
    /// @param amount The amount to decrease the reward by
    function decreaseRewardPool(address archaeologist, uint256 amount)
        internal
    {
        AppStorage storage s = LibAppStorage.getAppStorage();

        // Revert if the amount is greater than the current reward
        if (amount > s.archaeologistRewards[archaeologist]) {
            revert LibErrors.NotEnoughReward(
                s.archaeologistRewards[archaeologist],
                amount
            );
        }

        // Decrease the rewards amount
        s.archaeologistRewards[archaeologist] -= amount;
    }

    /// @notice Increases the amount stored in the archaeologistRewards mapping for an
    /// archaeologist.
    /// @param amount The amount to increase the reward by
    /// @param archaeologist The address of the archaeologist whose
    /// reward is being increased
    function increaseRewardPool(address archaeologist, uint256 amount)
        internal
    {
        AppStorage storage s = LibAppStorage.getAppStorage();

        s.archaeologistRewards[archaeologist] += amount;
    }
}

contracts/libraries/LibUtils.sol.content:
// SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.13;

import "../storage/LibAppStorage.sol";
import "../libraries/LibTypes.sol";
import {LibErrors} from "../libraries/LibErrors.sol";

/**
 * @title Utility functions used within the Sarcophagus system
 * @notice This library implements various functions that are used throughout
 * Sarcophagus, mainly to DRY up the codebase
 * @dev these functions are all stateless, public, pure/view
 */
library LibUtils {
    /**
     * @notice Reverts if the public key length is not exactly 64 bytes long
     * @param publicKey the key to check length of
     */
    function publicKeyLength(bytes memory publicKey) public pure {
        require(publicKey.length == 64, "public key must be 64 bytes");
    }

    /**
     * @notice Reverts if the hash of singleHash does not equal doubleHash
     * @param doubleHash the hash to compare hash of singleHash to
     * @param singleHash the value to hash and compare against doubleHash
     */
    function hashCheck(bytes32 doubleHash, bytes memory singleHash)
        public
        pure
    {
        require(doubleHash == keccak256(singleHash), "hashes do not match");
    }

    /**
     * @notice Reverts if the input string is not empty
     * @param assetId the string to check
     */
    function confirmAssetIdNotSet(string memory assetId) public pure {
        require(bytes(assetId).length == 0, "assetId has already been set");
    }

    /**
     * @notice Reverts if existing assetId is not empty, or if new assetId is
     * @param existingAssetId the orignal assetId to check, make sure is empty
     * @param newAssetId the new assetId, which must not be empty
     */
    function assetIdsCheck(
        string memory existingAssetId,
        string memory newAssetId
    ) public pure {
        // verify that the existingAssetId is currently empty
        confirmAssetIdNotSet(existingAssetId);

        require(bytes(newAssetId).length > 0, "assetId must not have 0 length");
    }

    function archaeologistUnwrappedCheck(bytes32 sarcoId, address archaeologist)
        internal
        view
    {
        if (
            getArchaeologist(sarcoId, archaeologist).unencryptedShard.length > 0
        ) {
            revert LibErrors.ArchaeologistAlreadyUnwrapped(archaeologist);
        }
    }

    /**
     * @notice Given some bytes32 data, a signature, and an account, verify that the
     * identifier was signed by the account.
     * @dev The verifyBytes32Signature function is identical to the
     * verifyBytesSignature function except for the data type being passed in.
     * The reason these are split up is beacuse it's really tricky to convert a
     * bytes32 value into a bytes value and have ecrecover still work properly.
     * If a simple solution can be found for this problem then please combine
     * these two functions together.
     * @param data the data to verify
     * @param v signature element
     * @param r signature element
     * @param s signature element
     * @param account address to confirm data and signature came from
     */
    function verifyBytes32Signature(
        bytes32 data,
        uint8 v,
        bytes32 r,
        bytes32 s,
        address account
    ) internal pure {
        // Hash the hash of the data payload
        bytes32 messageHash = keccak256(
            abi.encodePacked(
                "\x19Ethereum Signed Message:\n32",
                keccak256(abi.encode(data))
            )
        );

        // Genearate the address from the signature.
        // ecrecover should always return a valid address.
        address hopefulAddress = ecrecover(messageHash, v, r, s);

        if (hopefulAddress != account) {
            revert LibErrors.SignatureFromWrongAccount(hopefulAddress, account);
        }
    }

    /**
     * @notice Given an identifier, a signature, and an account, verify that the
     * identifier was signed by the account.
     * @dev The verifyBytes32Signature function is identical to the
     * verifyBytesSignature function except for the data type being passed in.
     * The reason these are split up is beacuse it's really tricky to convert a
     * bytes32 value into a bytes value and have ecrecover still work properly.
     * If a simple solution can be found for this problem then please combine
     * these two functions together.
     * @param data the data to verify
     * @param v signature element
     * @param r signature element
     * @param s signature element
     * @param account address to confirm data and signature came from
     */
    function verifyBytesSignature(
        bytes memory data,
        uint8 v,
        bytes32 r,
        bytes32 s,
        address account
    ) internal pure {
        // Hash the hash of the data payload
        bytes32 messageHash = keccak256(
            abi.encodePacked(
                "\x19Ethereum Signed Message:\n32",
                keccak256(abi.encode(data))
            )
        );

        // Genearate the address from the signature.
        // ecrecover should always return a valid address.
        // It's highly recommended that a hash be passed into ecrecover
        address hopefulAddress = ecrecover(messageHash, v, r, s);

        if (hopefulAddress != account) {
            revert LibErrors.SignatureFromWrongAccount(hopefulAddress, account);
        }
    }

    /// @notice Returns the address that signed some data given the data and the
    /// signature.
    /// @param data the data to verify
    /// @param v signature element
    /// @param r signature element
    /// @param s signature element
    /// @return the address that signed the data
    function recoverAddress(
        bytes memory data,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) internal pure returns (address) {
        // Hash the hash of the data payload
        bytes32 messageHash = keccak256(
            abi.encodePacked(
                "\x19Ethereum Signed Message:\n32",
                keccak256(abi.encode(data))
            )
        );

        // Genearate the address from the signature.
        // ecrecover should always return a valid address.
        // It's highly recommended that a hash be passed into ecrecover
        address account = ecrecover(messageHash, v, r, s);

        return account;
    }

    /**
     * @notice Reverts if the given resurrection time is not in the future
     * @param resurrectionTime the time to check against block.timestamp
     */
    function resurrectionInFuture(uint256 resurrectionTime) internal view {
        if (resurrectionTime <= block.timestamp) {
            revert LibErrors.ResurrectionTimeInPast(resurrectionTime);
        }
    }

    /**
     * @notice Calculates the grace period that an archaeologist has after a
     * sarcophagus has reached its resurrection time
     * @param resurrectionTime the resurrection timestamp of a sarcophagus
     * @return the grace period
     * @dev The grace period is dependent on how far out the resurrection time
     * is. The longer out the resurrection time, the longer the grace period.
     * There is a minimum grace period of 30 minutes, otherwise, it's
     * calculated as 1% of the time between now and resurrection time.
     */
    function getGracePeriod(uint256 resurrectionTime)
        internal
        view
        returns (uint256)
    {
        // set a minimum window of 30 minutes
        uint16 minimumResurrectionWindow = 30 minutes;

        // calculate 1% of the relative time between now and the resurrection
        // time
        uint256 gracePeriod = (
            resurrectionTime > block.timestamp
                ? resurrectionTime - block.timestamp
                : block.timestamp - resurrectionTime
        ) / 100;

        // if our calculated grace period is less than the minimum time, we'll
        // use the minimum time instead
        if (gracePeriod < minimumResurrectionWindow) {
            gracePeriod = minimumResurrectionWindow;
        }

        // return that grace period
        return gracePeriod;
    }

    /**
     * @notice Reverts if we're not within the resurrection window (on either
     * side)
     * @param resurrectionTime the resurrection time of the sarcophagus
     * (absolute, i.e. a date time stamp)
     */
    function unwrapTime(uint256 resurrectionTime) internal view {
        // revert if too early
        if (resurrectionTime > block.timestamp) {
            revert LibErrors.TooEarlyToUnwrap(
                resurrectionTime,
                block.timestamp
            );
        }

        uint256 resurrectionWindow = getGracePeriod(resurrectionTime);

        // revert if too late
        if (resurrectionTime + resurrectionWindow < block.timestamp) {
            revert LibErrors.TooLateToUnwrap(
                resurrectionTime,
                resurrectionWindow,
                block.timestamp
            );
        }
    }

    /// @notice Checks if the archaeologist exists on the sarcophagus.
    /// @param sarcoId the identifier of the sarcophagus
    /// @param archaeologist the address of the archaeologist
    /// @return The boolean true if the archaeologist exists on the sarcophagus
    function archaeologistExistsOnSarc(bytes32 sarcoId, address archaeologist)
        internal
        view
        returns (bool)
    {
        AppStorage storage s = LibAppStorage.getAppStorage();

        // If the doubleHashedShard on an archaeologist is 0 (which is its default value),
        // then the archaeologist doesn't exist on the sarcophagus
        return
            s
            .sarcophagusArchaeologists[sarcoId][archaeologist]
                .doubleHashedShard != 0;
    }

    /// @notice Gets an archaeologist given the sarcophagus identifier and the
    /// archaeologist's address.
    /// @param sarcoId the identifier of the sarcophagus
    /// @param archaeologist the address of the archaeologist
    /// @return The archaeologist
    function getArchaeologist(bytes32 sarcoId, address archaeologist)
        internal
        view
        returns (LibTypes.ArchaeologistStorage memory)
    {
        AppStorage storage s = LibAppStorage.getAppStorage();

        return s.sarcophagusArchaeologists[sarcoId][archaeologist];
    }

    /// @notice Checks if a sarcophagus has been finalized by checking if it
    /// contains any arweaveTxIds.
    /// @param sarcoId the identifier of the sarcophagus
    /// @return The boolean true if the sarcophagus has been finalized
    function isSarcophagusFinalized(bytes32 sarcoId)
        internal
        view
        returns (bool)
    {
        AppStorage storage s = LibAppStorage.getAppStorage();

        return s.sarcophagi[sarcoId].arweaveTxIds.length > 0;
    }

    /// @notice Calculates the protocol fees to be taken from the embalmer.
    /// @return The protocol fees amount
    function calculateProtocolFee() internal view returns (uint256) {
        AppStorage storage s = LibAppStorage.getAppStorage();

        // TODO: Need feedback from the community to determine how protocol fees should be calculated
        // Just returns a constant value defined in an env file
        return s.protocolFee;
    }
}

contracts/storage/LibAppStorage.sol.content:
// SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.13;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "../libraries/LibTypes.sol";

// Global storage for the app. Can be accessed in facets and in libraries
struct AppStorage {
    IERC20 sarcoToken;
    // The amount to be taken from the embalmer each time a protocol fee should
    // be collected
    uint256 protocolFee;
    // The amount of protocol fees currently stored on the contract
    uint256 totalProtocolFees;
    // sarcophagi
    bytes32[] sarcophagusIdentifiers;
    // Each archaeologist's total free and cursed bonds
    mapping(address => uint256) freeBonds;
    mapping(address => uint256) cursedBonds;
    // archaeologist stats
    mapping(address => mapping(bytes32 => bool)) archaeologistSuccesses;
    mapping(address => bytes32[]) archaeologistCancels;
    mapping(address => bytes32[]) archaeologistAccusals;
    mapping(address => bytes32[]) archaeologistCleanups;
    // Track how much archaeologists have made. To be credited and debited
    // as archaeologists fulfil their duties and withdraw their rewards
    mapping(address => uint256) archaeologistRewards;
    mapping(bytes32 => LibTypes.Sarcophagus) sarcophagi;
    // sarcophagus ownerships
    mapping(address => bytes32[]) embalmerSarcophagi;
    mapping(address => bytes32[]) archaeologistSarcophagi;
    mapping(address => bytes32[]) recipientSarcophagi;
    // Mapping of unencrypted shard double hashes to archaeologists who are
    // responsible for them. Needed to optimise Accuse algo - unencrypted shard is
    // double hashed and used as a constant O(1) lookup here
    mapping(bytes32 => address) doubleHashedShardArchaeologists;
    // A mapping used to store an archaeologist's data on a sarcophagus.
    // Bounty, digging fees, storage fees, and the hashed shards of the
    // archaeologists all need to be stored per sarcophagus. This mapping of a
    // mapping stores the archaeologist's data we need per sarcophagus.
    // Example usage (to retrieve the bounty an archaeologist may claim on some sarcophagus):
    //   LibTypes.ArchaeologistStorage bondedArchaeologist = sarcophagusArchaeologists[sarcoId][archAddress];
    //   uint256 bounty = bondedArchaeologist.bounty;
    mapping(bytes32 => mapping(address => LibTypes.ArchaeologistStorage)) sarcophagusArchaeologists;
}

library LibAppStorage {
    function getAppStorage() internal pure returns (AppStorage storage s) {
        // Set the position of our struct in contract storage
        // Since AppStorage s is the first and only state variable declared in
        // facets its position in contract storage is 0
        assembly {
            s.slot := 0
        }
    }
}
